function get(name) {
  return localStorage.getItem(name) || sessionStorage.getItem(name);
}
function set(name, value, remember = true) {
  if (remember) localStorage.setItem(name, value);
  else sessionStorage.setItem(name, value);
}
function del(name) {
  localStorage.removeItem(name);
  sessionStorage.removeItem(name);
}

export const tokens = {
  get access() {
    return get("accessToken");
  },
  set access(v) {
    v ? set("accessToken", v, true) : del("accessToken");
  },
  get refresh() {
    return get("refreshToken");
  },
  set refresh(v) {
    v ? set("refreshToken", v, true) : del("refreshToken");
  },
  get user() {
    const raw = get("user");
    try {
      return raw ? JSON.parse(raw) : null;
    } catch {
      return null;
    }
  },
  set user(v) {
    v ? set("user", JSON.stringify(v), true) : del("user");
  },
  clear() {
    this.access = null;
    this.refresh = null;
    this.user = null;
  },
};

