export const authApi = {
  async login(login, password) {
    const data = await apiFetch("/auth/login", {
      method: "POST",
      body: JSON.stringify({ login, password }),
    });
    if (data?.accessToken) tokens.access = data.accessToken;
    if (data?.refreshToken) tokens.refresh = data.refreshToken;
    if (data?.user) tokens.user = data.user;
    return data;
  },
  async logout() {
    try {
      await apiFetch("/auth/logout", { method: "POST" });
    } catch {}
    tokens.clear();
    location.href = "/login";
  },
};

